import React, { useState } from "react";
import "./Sidebar.css"; // Assuming your CSS file is named Sidebar.css

const Sidebar = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  return (
    <>
      <div className="navbar">
        <div className="navbar-brand">Nevin Ada Ã‡akmak</div>
        <div className="nav-links">
          <a href="#home">Home</a>
          <a href="#resume">Resume</a>
          <a href="#content">Content</a>
        </div>
        <div className="menu-icon" onClick={toggleSidebar}>
          &#9776;
        </div>
      </div>

      <div className={`sidebar ${isSidebarOpen ? "active" : ""}`}>
        <a href="#home" onClick={toggleSidebar}>
          Home
        </a>
        <a href="#resume" onClick={toggleSidebar}>
          Resume
        </a>
        <a href="#content" onClick={toggleSidebar}>
          Content
        </a>
        <div className="social-media-icons">
          <a href="#">&#xf099;</a>
          <a href="#">&#xf09a;</a>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
