import React, { useCallback, useEffect, useState } from "react";
import useEmblaCarousel from "embla-carousel-react";
import AutoScroll from "embla-carousel-auto-scroll";

const EmblaCarousel = ({ options }) => {
  const [emblaRef, emblaApi] = useEmblaCarousel(options, [
    AutoScroll({ playOnInit: false }),
  ]);
  const [isPlaying, setIsPlaying] = useState(false);

  const slides = [
    { src: "/images/amacss_photo1.JPG" },
    { src: "/images/chess_1.gif" },
    { src: "/images/dog_photo1.jpg" },
    { src: "/images/dog_photo2.jpeg" },
    { src: "/images/drawing_photo1.jpeg" },
    { src: "/images/drawing_photo2.jpeg" },
    { src: "/images/frc_photo1.jpg" },
    { src: "/images/frc_photo2.JPG" },
    { src: "/images/montreal_photo1.jpeg" },
    { src: "/images/montreal_photo2.jpeg" },
    { src: "/images/montreal_photo3.jpeg" },
    { src: "/images/moon_photo1.jpg" },
    { src: "/images/moon_photo2.jpeg" },
    { src: "/images/moon_photo3.jpeg" },
    { src: "/images/moon_photo4.jpeg" },
    { src: "/images/moon_photo5.jpeg" },
    { src: "/images/moon_photo6.jpeg" },
    { src: "/images/moon_photo7.jpeg" },
    { src: "/images/robotics_photo1.jpeg" },
    { src: "/images/robotics_photo2.jpeg" },
    { src: "/images/toronto_photo1.jpeg" },
    { src: "/images/toronto_photo2.jpeg" },
    { src: "/images/turkey_photo1.jpeg" },
    { src: "/images/turkey_photo2.jpeg" },
    { src: "/images/turkey_photo3.jpeg" },
    { src: "/images/volunteering_photo1.jpeg" },
    { src: "/images/volunteering_photo2.jpeg" },
  ];

  const toggleAutoplay = useCallback(() => {
    const autoScroll = emblaApi?.plugins()?.autoScroll;
    if (!autoScroll) return;

    const playOrStop = autoScroll.isPlaying()
      ? autoScroll.stop
      : autoScroll.play;
    playOrStop();
  }, [emblaApi]);

  useEffect(() => {
    const autoScroll = emblaApi?.plugins()?.autoScroll;
    if (!autoScroll) return;

    setIsPlaying(autoScroll.isPlaying());
    emblaApi
      .on("autoScroll:play", () => setIsPlaying(true))
      .on("autoScroll:stop", () => setIsPlaying(false))
      .on("reInit", () => setIsPlaying(autoScroll.isPlaying()));
  }, [emblaApi]);

  return (
    <div className="embla">
      <div className="embla__viewport" ref={emblaRef}>
        <div className="embla__container">
          {slides.map((slide, index) => (
            <div className="embla__slide" key={index}>
              <img
                src={slide.src}
                alt={`Slide ${index + 1}`}
                className="embla__slide__img"
              />
            </div>
          ))}
        </div>
      </div>
      <div className="embla__controls">
        <button className="embla__play" onClick={toggleAutoplay} type="button">
          {isPlaying ? "Stop" : "Start"}
        </button>
      </div>
    </div>
  );
};

export default EmblaCarousel;
