import React, { useEffect, useState } from "react";
import useEmblaCarousel from "embla-carousel-react";
import AutoScroll from "embla-carousel-auto-scroll";

const EmblaCarousel = ({ options }) => {
  const [emblaRef, emblaApi] = useEmblaCarousel(
    {
      ...options,
      loop: true,
      skipSnaps: false,
    },
    []
  );

  const [isPlaying, setIsPlaying] = useState(false); // Set initial value to false

  const slides = [
    // slides array remains the same
  ];

  const shuffledSlides = slides.sort(() => Math.random() - 0.5);

  useEffect(() => {
    if (!emblaApi) return;

    if (isPlaying) {
      emblaApi.reInit({ AutoScroll: { stopOnInteraction: true } });
    } else {
      emblaApi.reInit({ AutoScroll: { stopOnInteraction: false } });
    }

    return () => {
      if (isPlaying) {
        emblaApi.reInit({ AutoScroll: { stopOnInteraction: true } });
        setIsPlaying(false);
      }
    };
  }, [emblaApi, isPlaying]);

  const handleStartButtonClick = () => {
    setIsPlaying(true);
  };

  return (
    <div className="photo-album-container">
      <div className="photo-album-header">
        <div className="photo-album-title">Photo Album</div>
        <button className="start-button" onClick={handleStartButtonClick}>
          Start
        </button>
      </div>
      <div className="embla">
        <div className="embla__viewport" ref={emblaRef}>
          <div className="embla__container">
            {shuffledSlides.map((slide, index) => (
              <div className="embla__slide" key={index}>
                <img
                  src={`/images/${slide}`}
                  alt={`Slide ${index + 1}`}
                  className="embla__slide__img"
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmblaCarousel;
