import React from "react";
import { Document, Page } from "react-pdf";
import { FaDownload } from "react-icons/fa";

function Resume() {
  return (
    <div
      style={{
        padding: "20px",
        maxWidth: "1200px",
        margin: "auto",
        backgroundColor: "var(--background)",
        color: "var(--text)",
      }}
    >
      <h1 style={{ color: "var(--primary)", textAlign: "center" }}>
        My Resume
      </h1>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: "20px",
          width: "100%",
        }}
      >
        <div style={{ width: "100%", height: "90vh", overflow: "auto" }}>
          <Document
            file="/resume.pdf"
            onLoadSuccess={({ numPages }) =>
              console.log(`Loaded a document with ${numPages} pages.`)
            }
          >
            <Page pageNumber={1} width={window.innerWidth * 0.8} />
          </Document>
        </div>
        <a
          href="/resume.pdf"
          download
          style={{
            display: "flex",
            alignItems: "center",
            padding: "10px 20px",
            fontSize: "16px",
            color: "var(--text)",
            backgroundColor: "var(--primary)",
            textDecoration: "none",
            borderRadius: "5px",
            border: "1px solid var(--primary)",
            boxShadow: "0 4px 6px rgba(0,0,0,0.1)",
            whiteSpace: "nowrap",
          }}
        >
          <FaDownload style={{ marginRight: "8px" }} />
          Download Resume
        </a>
      </div>
    </div>
  );
}

export default Resume;
