import React, { useCallback, useEffect, useState } from "react";
import useEmblaCarousel from "embla-carousel-react";
import AutoScroll from "embla-carousel-auto-scroll";
import "./EmblaCarousel.css";

const EmblaCarousel = ({ options }) => {
  const [emblaRef, emblaApi] = useEmblaCarousel(options, [
    AutoScroll({ playOnInit: false }),
  ]);
  const [isPlaying, setIsPlaying] = useState(false);

  const slides = [
    "amacss_photo1.JPG",
    "chess_1.gif",
    "dog_photo1.jpg",
    "dog_photo2.jpeg",
    "drawing_photo1.jpeg",
    "drawing_photo2.jpeg",
    "frc_photo1.jpg",
    "frc_photo2.JPG",
    "montreal_photo1.jpeg",
    "montreal_photo2.jpeg",
    "montreal_photo3.jpeg",
    "moon_photo1.jpg",
    "moon_photo2.jpeg",
    "moon_photo3.jpeg",
    "moon_photo4.jpeg",
    "moon_photo5.jpeg",
    "moon_photo6.jpeg",
    "moon_photo7.jpeg",
    "robotics_photo1.jpeg",
    "robotics_photo2.jpeg",
    "toronto_photo1.jpeg",
    "toronto_photo2.jpeg",
    "turkey_photo1.jpeg",
    "turkey_photo2.jpeg",
    "turkey_photo3.jpeg",
    "volunteering_photo1.jpeg",
    "volunteering_photo2.jpeg",
  ];

  const shuffledSlides = slides.sort(() => Math.random() - 0.5);

  const toggleAutoplay = useCallback(() => {
    const autoScroll = emblaApi?.plugins()?.autoScroll;
    if (!autoScroll) return;

    const playOrStop = autoScroll.isPlaying()
      ? autoScroll.stop
      : autoScroll.play;
    playOrStop();
  }, [emblaApi]);

  useEffect(() => {
    const autoScroll = emblaApi?.plugins()?.autoScroll;
    if (!autoScroll) return;

    setIsPlaying(autoScroll.isPlaying());
    emblaApi
      .on("autoScroll:play", () => setIsPlaying(true))
      .on("autoScroll:stop", () => setIsPlaying(false))
      .on("reInit", () => setIsPlaying(autoScroll.isPlaying()));
  }, [emblaApi]);

  return (
    <div className="photo-album-container">
      <div className="photo-album-header">
        <div className="photo-album-title">Photo Album</div>
        <button className="embla__play" onClick={toggleAutoplay} type="button">
          {isPlaying ? "Stop" : "Start"}
        </button>
      </div>
      <div className="embla">
        <div className="embla__viewport" ref={emblaRef}>
          <div className="embla__container">
            {shuffledSlides.map((slide, index) => (
              <div className="embla__slide" key={index}>
                <img
                  src={`/images/${slide}`}
                  alt={`Slide ${index + 1}`}
                  className="embla__slide__img"
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmblaCarousel;
