import React, { useState, useEffect, useRef } from "react";

const EmblaCarousel = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const intervalRef = useRef(null);

  const slides = [
    "amacss_photo1.JPG",
    "chess_1.gif",
    "dog_photo1.jpg",
    "dog_photo2.jpeg",
    "drawing_photo1.jpeg",
    "drawing_photo2.jpeg",
    "frc_photo1.jpg",
    "frc_photo2.JPG",
    "montreal_photo1.jpeg",
    "montreal_photo2.jpeg",
    "montreal_photo3.jpeg",
    "moon_photo1.jpg",
    "moon_photo2.jpeg",
    "moon_photo3.jpeg",
    "moon_photo4.jpeg",
    "moon_photo5.jpeg",
    "moon_photo6.jpeg",
    "moon_photo7.jpeg",
    "robotics_photo1.jpeg",
    "robotics_photo2.jpeg",
    "toronto_photo1.jpeg",
    "toronto_photo2.jpeg",
    "turkey_photo1.jpeg",
    "turkey_photo2.jpeg",
    "turkey_photo3.jpeg",
    "volunteering_photo1.jpeg",
    "volunteering_photo2.jpeg",
  ];

  const shuffledSlides = slides.sort(() => Math.random() - 0.5);

  useEffect(() => {
    if (isPlaying) {
      intervalRef.current = setInterval(() => {
        setCurrentIndex((prevIndex) => {
          if (prevIndex === shuffledSlides.length - 1) {
            clearInterval(intervalRef.current);
            setIsPlaying(false);
            return prevIndex;
          }
          return prevIndex + 1;
        });
      }, 2000); // Change slide every 2 seconds
    } else {
      clearInterval(intervalRef.current);
    }

    return () => clearInterval(intervalRef.current);
  }, [isPlaying, shuffledSlides.length]);

  const handleStart = () => {
    setIsPlaying(true);
  };

  return (
    <div className="photo-album-container">
      <div className="photo-album-header">
        <div className="photo-album-title">Photo Album</div>
        <button
          className="embla__play"
          onClick={handleStart}
          type="button"
          disabled={isPlaying}
        >
          {isPlaying ? "Playing..." : "Start"}
        </button>
      </div>
      <div className="embla">
        <div className="embla__viewport">
          <div
            className="embla__container"
            style={{
              transform: `translateX(-${currentIndex * 100}%)`,
              transition: "transform 0.5s ease",
            }}
          >
            {shuffledSlides.map((slide, index) => (
              <div className="embla__slide" key={index}>
                <img
                  src={`/images/${slide}`}
                  alt={`Slide ${index + 1}`}
                  className="embla__slide__img"
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmblaCarousel;
